import React, { useState } from 'react';

const StudentLogin = ({ onLogin }) => {
  const [studentId, setStudentId] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!studentId.trim() || !password.trim()) {
      setError('Please enter both Student ID and password');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://127.0.0.1:5000/student/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          student_id: studentId,
          password: password
        })
      });

      const data = await response.json();

      if (response.ok && data.success) {
        onLogin(data.student);
      } else {
        setError(data.message || 'Login failed');
      }
    } catch (error) {
      setError('Connection error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-gradient-to-br from-blue-50 to-slate-100 p-4">
      <div className="w-full max-w-md bg-white rounded-2xl shadow-lg p-8">
        <div className="text-center mb-6">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">üéì Student Portal</h1>
          <p className="text-gray-500">Login to access support services</p>
        </div>

        <form onSubmit={handleLogin} className="space-y-5">
          <div>
            <label className="block text-gray-700 font-semibold mb-2 text-sm">
              Student ID:
            </label>
            <input
              type="text"
              value={studentId}
              onChange={(e) => setStudentId(e.target.value)}
              placeholder="e.g., STU001"
              className="w-full px-4 py-2 border-2 border-gray-200 rounded-md text-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition"
            />
          </div>

          <div>
            <label className="block text-gray-700 font-semibold mb-2 text-sm">
              Password:
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              className="w-full px-4 py-2 border-2 border-gray-200 rounded-md text-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition"
            />
          </div>

          {error && (
            <div className="bg-red-100 text-red-700 px-3 py-2 rounded-md text-sm border border-red-300">
              ‚ùå {error}
            </div>
          )}

          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 rounded-md text-white font-semibold text-lg transition 
              ${loading
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-blue-600 hover:bg-blue-700 active:scale-[0.98]'
              }`}
          >
            {loading ? 'üîÑ Logging in...' : 'üîë Login'}
          </button>
        </form>

        <div className="mt-8 bg-blue-50 rounded-md p-4 text-sm text-gray-700">
          <strong className="block mb-2">üìã Demo Accounts:</strong>
          <div className="space-y-1">
            <div>üë§ STU001 / student123</div>
            <div>üë§ STU002 / student123</div>
            <div>üë§ STU003 / student123</div>
          </div>
        </div>

        <div className="text-center mt-6">
          <a
            href="/admin"
            className="text-gray-500 text-sm hover:text-gray-700 transition"
          >
            üîß Admin Login
          </a>
        </div>
      </div>
    </div>
  );
};

export default StudentLogin;
